  #{creator,jdbcType=INTEGER}, #{comment_count,jdbcType=INTEGER}, #{view_count,jdbcType=INTEGER}, 
  #{like_count,jdbcType=INTEGER}, #{tag,jdbcType=VARCHAR}, #{description,jdbcType=LONGVARCHAR}
  )
